// filepath: src/components/UI/ProductCard.jsx
import React from "react";
import { Link } from "react-router-dom";        // ← fix import
import "../../styles/product-card.css";

const ProductCard = ({ item }) => {
  const { product_code, title, image01, price } = item;
  const imgUrl = `${process.env.PUBLIC_URL}/images/${image01}`;

  return (
    <div className="product__item">
      <div className="product__img">
        <img src={imgUrl} alt={title} className="w-100" />
      </div>
      <div className="product__content">
        <h5>
          <Link to={`/fooddetail/${product_code}`}>{title}</Link>
        </h5>
        <div className="d-flex align-items-center justify-content-between">
          <span className="product__price">${price}</span>
          <button className="addTOCart__btn">Add to Cart</button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
